    private void CalculateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CalculateActionPerformed
        // TODO add your handling code here:
        try {
        int divisor = Integer.parseInt(jTextField3.getText());
        String numbersText = jTextField2.getText();
        String[] numbersArray = numbersText.split("\\s+");

        int sum = 0;
        for (String numStr : numbersArray) {
            sum += Integer.parseInt(numStr);
        }

        if (divisor != 0) {
            double result = (double) sum / divisor;
            jTextField4.setText(Double.toString(result));
        } else {
            // Dividing by zero is not allowed
            jTextField4.setText("Cannot divide by zero");
        }
    } catch (NumberFormatException ex) {
        // Handle if the user entered non-numeric values
        jTextField4.setText("Invalid input");
        javax.swing.JOptionPane.showMessageDialog(this, "Please enter valid numbers.", "Invalid Input", javax.swing.JOptionPane.ERROR_MESSAGE);
    }
    }//GEN-LAST:event_CalculateActionPerformed
  
